import React from "react";
import './style.css';
import { useState } from "react";

interface ButtonClick {
  value: string;
}


const SquareButton: React.FC<ButtonClick> = ({value}) =>{
  // const [value, setValue] = useState<any>(null);
  // const clickHandler = () => {
  //   setValue('X');
  // }  
  return <button className="square" >{value}</button>;
};

export default function Board() {
  const [squares, setSquare] = useState<any>(Array(9).fill(null));
  const clickHandler = () => {
      setSquare('X');
    }  
  return (
    <>
      <div className="row">
        <SquareButton value={squares[0]} />
        <SquareButton value={squares[1]} />
        <SquareButton value={squares[2]} />
      </div>
      <div className="row">
        <SquareButton value={squares[3]} />
        <SquareButton value={squares[4]} />
        <SquareButton value={squares[5]} />
      </div>
      <div className="row">
        <SquareButton value={squares[6]} />
        <SquareButton value={squares[7]} />
        <SquareButton value={squares[8]} />
      </div>
    </>
  );
}
