import React from "react";
import './style.css';
import { useState } from "react";

interface ButtonClick {
  value: string;
  fun: () => void;
}


const SquareButton: React.FC<ButtonClick> = ({value, fun}) =>{
  return <button className="square" onClick={fun} >{value}</button>;
};

export default function Board() {
  const [squares, setSquare] = useState<any>(Array(9).fill(null));
  const clickHandler = (i: number) => {
      const copy = squares.slice();
      copy[i] = 'X';
      setSquare(copy);
    }  
  return (
    <>
      <div className="row">
        <SquareButton value={squares[0]} fun={clickHandler(0)}/>
        <SquareButton value={squares[1]} fun={clickHandler}/>
        <SquareButton value={squares[2]} fun={clickHandler}/>
      </div>
      <div className="row">
        <SquareButton value={squares[3]} fun={clickHandler}/>
        <SquareButton value={squares[4]} fun={clickHandler}/>
        <SquareButton value={squares[5]} fun={clickHandler}/>
      </div>
      <div className="row">
        <SquareButton value={squares[6]} fun={clickHandler}/>
        <SquareButton value={squares[7]} fun={clickHandler}/>
        <SquareButton value={squares[8]} fun={clickHandler}/>
      </div>
    </>
  );
}
